set award_id = #{awardId,jdbcType=BIGINT},
  user_id = #{userId,jdbcType=INTEGER},
  received = #{received,jdbcType=BIT},
  mtime = #{mtime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
